import React from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import {Gallery, Item} from "react-photoswipe-gallery";
import 'photoswipe/dist/photoswipe.css'
import 'photoswipe/dist/default-skin/default-skin.css'
import {useTypedSelector} from "../../types/redux.types";

const List = (): JSX.Element => {
    const list = useTypedSelector((state) => state.photos.list);
    const allCountPhotos = useTypedSelector((state) => state.photos.allCount);

    return (
        <div className='GalleryGrid'>
            <Gallery>
                <div className="grid-gallery">
                    <InfiniteScroll
                        dataLength={list.length}
                        next={() => {
                        }}
                        hasMore={allCountPhotos > list.length}
                        loader={<div/>}
                    >
                        {list.map(({urls, user, id, width, height, description}) =>
                            <Item
                                key={id}
                                original={urls.full}
                                thumbnail={urls.small}
                                width={width}
                                height={height}
                            >
                                {({ref, open}) => (
                                    <div className="destination" ref={ref} onClick={open} style={{backgroundImage: `url(${item.urls.small})`}}>
                                        <div className="overlay">
                                            <div className="user">
                                                <img src={item.user.profile_image.small} alt=""/>
                                                <Link to={`/users/${item.user.username}`}>{item.user.name}</Link>
                                            </div>
                                            <div className="actions">
                                                <div
                                                    className={`like ${item.liked_by_user ? 'active': ''}`}
                                                    onClick={(e) => toggleLike(e, item.liked_by_user, item.id)}
                                                >
                                                    <Tooltip title={`${item.liked_by_user ? 'Unlike' : 'Like'}`}>
                                                        <LikeIcon fill='red'/>
                                                    </Tooltip>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </Item>
                        )}
                    </InfiniteScroll>
                </div>
            </Gallery>
        </div>
    )
}

export default List;
