import axios from "axios";
import {IChangePhotoLikeRequest, IFetchPhotosQuery, IGetPhotosResponse} from "../interfaces/unsplash.interface";
import qs from "qs";

const instance = axios.create({
    baseURL: 'https://api.unsplash.com',
})

class UnsplashApi {

    async fetchPhotos(params: IFetchPhotosQuery): Promise<IGetPhotosResponse> {
        const queryParams = qs.stringify(params, {encode: false});

        return instance
            .get(`/search/photos?${queryParams}`)
            .then(response => response.data)
    }

    async changeLikePhoto ({id, isLiked}: IChangePhotoLikeRequest) {
        const url = `/photos/${id}/like`;

        if(isLiked){
            return instance.post(url)
                .then(response => response.data)
        }

        return instance.delete(url)
            .then(response => response.data)
    }
}

export const unsplashAPI = new UnsplashApi();
